import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Book = () => {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ title: '', author: '' });

  // Fetch the list of books from the backend
  useEffect(() => {
    axios
      .get('http://127.0.0.1:5000/api/books')  // Make sure this URL is correct based on your Flask server
      .then((response) => setBooks(response.data))
      .catch((error) => console.error('There was an error fetching the books:', error));
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewBook((prevBook) => ({
      ...prevBook,
      [name]: value,
    }));
  };

  // Handle form submission to add a new book
  const handleAddBook = (e) => {
    e.preventDefault();
    if (!newBook.title || !newBook.author) {
      alert('Please enter both title and author');
      return;
    }

    // Send the new book to the backend
    axios
      .post('http://127.0.0.1:5000/api/books', newBook)
      .then((response) => {
        setBooks((prevBooks) => [...prevBooks, response.data]); // Update the list of books
        setNewBook({ title: '', author: '' }); // Reset the form
      })
      .catch((error) => console.error('There was an error adding the book:', error));
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Book Page</h1>
      <div className="mb-4">
        <h2>Add a New Book</h2>
        <form onSubmit={handleAddBook}>
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              className="form-control"
              id="title"
              name="title"
              value={newBook.title}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="author">Author</label>
            <input
              type="text"
              className="form-control"
              id="author"
              name="author"
              value={newBook.author}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">Add Book</button>
        </form>
      </div>

      <h2 className="mb-4">Books List</h2>
      <ul className="list-group">
        {books.map((book) => (
          <li key={book.id} className="list-group-item d-flex justify-content-between align-items-center">
            {book.title} by {book.author}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Book;
