import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles.css'

const Book = () => {
    const [books, setBooks] = useState([]);
    const [newBook, setNewBook] = useState({ title: '', author: '' });

    // Fetch the list of books from the backend
    useEffect(() => {
        axios
            .get('http://127.0.0.1:8000/api/books/')  // Make sure this URL is correct based on your Flask server
            .then((response) => setBooks(response.data))
            .catch((error) => console.error('There was an error fetching the books:', error));
    }, []);

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewBook((prevBook) => ({
            ...prevBook,
            [name]: value,
        }));
    };

    // Handle form submission to add a new book
    const handleAddBook = (e) => {
        e.preventDefault();
        if (!newBook.title || !newBook.author) {
            alert('Please enter both title and author');
            return;
        }

        // Send the new book to the backend
        axios
            .post('http://127.0.0.1:8000/api/books/', newBook)
            .then((response) => {
                setBooks((prevBooks) => [...prevBooks, response.data]); // Update the list of books
                setNewBook({ title: '', author: '' }); // Reset the form
            })
            .catch((error) => console.error('There was an error adding the book:', error));
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-5 text-primary">
                Book Management
            </h1>

            <div className="card mb-4 shadow-sm">
                <div className="card-header bg-info text-white">
                    <h2 className="mb-0">Add a New Book</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleAddBook}>
                        <div className="form-row">
                            <div className=" col-md-3">
                                <label htmlFor="title">Title</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="title"
                                    name="title"
                                    value={newBook.title}
                                    onChange={handleInputChange}
                                    placeholder="Enter book title"
                                    required
                                />
                            </div>
                            <div className=" col-md-3">
                                <label htmlFor="author">Author</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="author"
                                    name="author"
                                    value={newBook.author}
                                    onChange={handleInputChange}
                                    placeholder="Enter author name"
                                    required
                                />
                            </div>
                            <div className=" col-md-3">
                                <label htmlFor="quantity">Quantity</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    id="quantity"
                                    name="quantity"
                                    value={newBook.quantity}
                                    onChange={handleInputChange}
                                    placeholder="Enter book quantity"
                                    required
                                />
                            </div>                            
                            <div className=" col-md-3">
                                <button type="submit" className="btn btn-success mt-3">
                                    Add Book
                                </button>
                            </div>
                        </div>


                    </form>
                </div>
            </div>


            <div className="card shadow-sm">
                <div className="card-header bg-primary text-white">
                    <h2 className="mb-0">Books List</h2>
                </div>
                <div className="card-body">
                    <table className="w-full table table-striped table-hover">
                        <thead>
                            <tr>
                                <th scope="col">#</th>
                                <th scope="col">Title</th>
                                <th scope="col">Author</th>
                                <th scope="col">Quantity</th>
                            </tr>
                        </thead>
                        <tbody>
                            {books.map((book) => (
                                <tr key={book.id}>
                                    <td>{book.id}</td>
                                    <td><strong>{book.title}</strong></td>
                                    <td><em>{book.author}</em></td>
                                    <td>{book.quantity}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            <div className="card mb-4 shadow-sm">
  <div className="card-header bg-info text-white">
    <h2 className="mb-0">Borrow Book</h2>
  </div>
  <div className="card-body">
    <form onSubmit={handleBorrowBook}>
      <div className="form-row">
        {/* Dropdown for selecting User */}
        <div className="col-md-3">
          <label htmlFor="user">Select User</label>
          <select
            className="form-control"
            id="user"
            name="user"
            value={selectedUser}
            onChange={handleUserChange}
            required
          >
            <option value="" disabled>
              Choose a user
            </option>
            {users.map((user) => (
              <option key={user.id} value={user.id}>
                {user.name}
              </option>
            ))}
          </select>
        </div>

        {/* Dropdown for selecting Book */}
        <div className="col-md-3">
          <label htmlFor="book">Select Book</label>
          <select
            className="form-control"
            id="book"
            name="book"
            value={selectedBook}
            onChange={handleBookChange}
            required
          >
            <option value="" disabled>
              Choose a book
            </option>
            {availableBooks.map((book) => (
              <option key={book.id} value={book.id}>
                {book.title}
              </option>
            ))}
          </select>
        </div>

        {/* Input for Quantity */}
        <div className="col-md-3">
          <label htmlFor="quantity">Quantity</label>
          <input
            type="number"
            className="form-control"
            id="quantity"
            name="quantity"
            value={quantity}
            onChange={handleQuantityChange}
            placeholder="Enter quantity"
            required
          />
        </div>

        {/* Submit Button */}
        <div className="col-md-3">
          <button type="submit" className="btn btn-success mt-4 w-100">
            Borrow Book
          </button>
        </div>
      </div>
    </form>
  </div>
</div>


        </div>
    );
};

export default Book;
