import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'

const Book = () => {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ title: '', author: '' });

  // Fetch the list of books from the backend
  useEffect(() => {
    axios
      .get('http://127.0.0.1:8000/books/books/')  // Make sure this URL is correct based on your Flask server
      .then((response) => setBooks(response.data))
      .catch((error) => console.error('There was an error fetching the books:', error));
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewBook((prevBook) => ({
      ...prevBook,
      [name]: value,
    }));
  };

  // Handle form submission to add a new book
  const handleAddBook = (e) => {
    e.preventDefault();
    if (!newBook.title || !newBook.author) {
      alert('Please enter both title and author');
      return;
    }

    // Send the new book to the backend
    axios
      .post('http://127.0.0.1:8000/books/books/', newBook)
      .then((response) => {
        setBooks((prevBooks) => [...prevBooks, response.data]); // Update the list of books
        setNewBook({ title: '', author: '' }); // Reset the form
      })
      .catch((error) => console.error('There was an error adding the book:', error));
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5 text-primary">Book Management</h1>

      <div className="card mb-4">
        <div className="card-header bg-info text-white">
          <h2 className="mb-0">Add a New Book</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleAddBook}>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                value={newBook.title}
                onChange={handleInputChange}
                placeholder="Enter book title"
                required
              />
            </div>
            <div className="form-group mt-3">
              <label htmlFor="author">Author</label>
              <input
                type="text"
                className="form-control"
                id="author"
                name="author"
                value={newBook.author}
                onChange={handleInputChange}
                placeholder="Enter author name"
                required
              />
            </div>
            <button type="submit" className="btn btn-success mt-3">
              Add Book
            </button>
          </form>
        </div>
      </div>

      <div className="card">
        <div className="card-header bg-primary text-white">
          <h2 className="mb-0">Books List</h2>
        </div>
        <div className="card-body">
          <ul className="list-group">
            {books.map((book) => (
              <li key={book.id} className="list-group-item d-flex justify-content-between align-items-center">
                <strong>{book.title}</strong> by <em>{book.author}</em>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Book;
