import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookPage = () => {
  const [books, setBooks] = useState([]);
  const [borrowedBooks, setBorrowedBooks] = useState([]);
  const [userId, setUserId] = useState(''); // assuming userId is already set or retrieved
  const [bookId, setBookId] = useState(''); // for storing the selected book ID

  // Fetch books from the backend
  useEffect(() => {
    axios
      .get('http://127.0.0.1:5000/api/books') // Adjust URL according to your backend
      .then((response) => setBooks(response.data))
      .catch((error) => console.error('Error fetching books:', error));
  }, []);

  // Handle borrow book
  const handleBorrowBook = (bookId) => {
    const selectedBook = books.find((book) => book.id === bookId);
    if (selectedBook.quantity > 0) {
      const borrowData = {
        userId: userId,
        bookId: selectedBook.id,
        borrowDate: new Date().toISOString(),
      };

      // Call backend API to record borrowing
      axios
        .post('http://127.0.0.1:5000/api/borrow', borrowData) // Adjust URL according to your backend
        .then((response) => {
          // Update the book's quantity locally
          setBooks((prevBooks) =>
            prevBooks.map((book) =>
              book.id === selectedBook.id
                ? { ...book, quantity: book.quantity - 1 }
                : book
            )
          );
          setBorrowedBooks((prevBorrowedBooks) => [
            ...prevBorrowedBooks,
            borrowData,
          ]);
        })
        .catch((error) => console.error('Error borrowing book:', error));
    } else {
      alert('This book is not available for borrowing');
    }
  };

  // Handle return book
  const handleReturnBook = (borrowedBookId) => {
    const borrowedBook = borrowedBooks.find((book) => book.bookId === borrowedBookId);
    if (borrowedBook) {
      const returnData = {
        userId: borrowedBook.userId,
        bookId: borrowedBook.bookId,
      };

      // Call backend API to record the return
      axios
        .post('http://127.0.0.1:5000/api/return', returnData) // Adjust URL according to your backend
        .then(() => {
          // Update the book's quantity locally
          setBooks((prevBooks) =>
            prevBooks.map((book) =>
              book.id === borrowedBook.bookId
                ? { ...book, quantity: book.quantity + 1 }
                : book
            )
          );
          setBorrowedBooks((prevBorrowedBooks) =>
            prevBorrowedBooks.filter((book) => book.bookId !== borrowedBookId)
          );
        })
        .catch((error) => console.error('Error returning book:', error));
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Books List</h1>

      {/* List Books */}
      <div className="card mb-4">
        <div className="card-header bg-info text-white">
          <h2 className="mb-0">Available Books</h2>
        </div>
        <div className="card-body">
          <table className="table table-striped">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Title</th>
                <th scope="col">Author</th>
                <th scope="col">Quantity</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.id}>
                  <th scope="row">{book.id}</th>
                  <td>{book.title}</td>
                  <td>{book.author}</td>
                  <td>{book.quantity}</td>
                  <td>
                    {book.quantity > 0 ? (
                      <button
                        className="btn btn-primary"
                        onClick={() => handleBorrowBook(book.id)}
                      >
                        Borrow
                      </button>
                    ) : (
                      <button className="btn btn-secondary" disabled>
                        Not Available
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* List Borrowed Books */}
      <div className="card mb-4">
        <div className="card-header bg-warning text-white">
          <h2 className="mb-0">Borrowed Books</h2>
        </div>
        <div className="card-body">
          <table className="table table-striped">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Book Title</th>
                <th scope="col">Borrow Date</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {borrowedBooks.map((borrowedBook) => (
                <tr key={borrowedBook.bookId}>
                  <th scope="row">{borrowedBook.bookId}</th>
                  <td>{books.find((book) => book.id === borrowedBook.bookId)?.title}</td>
                  <td>{new Date(borrowedBook.borrowDate).toLocaleDateString()}</td>
                  <td>
                    <button
                      className="btn btn-danger"
                      onClick={() => handleReturnBook(borrowedBook.bookId)}
                    >
                      Return
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default BookPage;
