import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
    return (
        <div className="container text-center mt-5">
            <h1 className="display-4 mb-5 font-weight-bold">Welcome to the Library Management System</h1>

            <div className="col-12 col-md-5 mb-3">
                {/* Button for Book Page */}
                <div>
                    <Link to="/book">
                        <button className="btn btn-primary btn-lg w-100 shadow-sm hover-btn">
                            <i className="fas fa-book mr-2"></i> Go to Book Page
                        </button>
                    </Link>
                </div>

                {/* Button for User Page */}
                <div>
                    <Link to="/user">
                        <button className="btn btn-success btn-lg w-100 shadow-sm hover-btn">
                            <i className="fas fa-user mr-2"></i> Go to User Page
                        </button>
                    </Link>
                </div>

                {/* Button for User Page */}
                <div>
                    <Link to="/borrow">
                        <button className="btn btn-success btn-lg w-100 shadow-sm hover-btn">
                            <i className="fas fa-user mr-2"></i> Borrow Book
                        </button>
                    </Link>
                </div>


            </div>
        </div>
    );
};

export default Home;
