import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles.css';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const BookPage = () => {
    const [books, setBooks] = useState([]);
    const [newBook, setNewBook] = useState({ title: '', author: '' });
    const [selectedUser, setSelectedUser] = useState("");
    const [selectedBook, setSelectedBook] = useState("");
    const [quantity, setQuantity] = useState("");
    const [users, setUsers] = useState('');
    const [availableBooks, setAvailableBooks] = useState("");
    const [successMessage, setSuccessMessage] = useState("");
    const [errorMessage, setErrorMessage] = useState("");

    console.log(toast.POSITION);  // This should print an object with properties like TOP_RIGHT


    // Handlers
    const handleUserChange = (e) => {
        setSelectedUser(e.target.value);
    };

    const handleBookChange = (e) => {
        setSelectedBook(e.target.value);
    };

    const handleQuantityChange = (e) => {
        setQuantity(e.target.value);
    };

    const handleBorrowBook = (e) => {
        e.preventDefault();
        const borrowData = {
            user: selectedUser,
            book: selectedBook,
            quantity,
        };

        console.log(borrowData, 'data');

        axios
            .post('http://127.0.0.1:8000/api/borrow/', borrowData)
            .then((response) => {
                setSuccessMessage(response.data.message); // Set success message
                setErrorMessage(""); // Clear any error message
                setBooks((prevBooks) => [...prevBooks, response.data]);
                setNewBook({ title: '', author: '' });
                toast.success("Book borrowed successfully!", {
                    position: toast.POSITION.TOP_RIGHT,
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                });
            })
            .catch((error) => {
                console.log(error.response, 'error.response');
                if (error.response) {
                    toast.error(error.response.data.error, {
                        position: toast.POSITION.TOP_RIGHT,
                        autoClose: 5000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                    });
                } else {
                    // Show network error toast
                    toast.error("An error occurred. Please try again.");
                }
            });
    };

    useEffect(() => {
        axios
            .get('http://127.0.0.1:8000/api/books/')
            .then((response) => {
                const booksData = response.data; // Ensure this is an array
                setBooks(booksData);
                setAvailableBooks(booksData);
            })
            .catch((error) =>
                console.error('There was an error fetching the books:', error)
            );
    }, []);

    useEffect(() => {
        axios
            .get('http://127.0.0.1:8000/api/users/')
            .then((response) => setUsers(response.data))
            .catch((error) => console.error('Error fetching users:', error));
    }, []);

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-5 text-primary">Library Management</h1>

            <div className="card mb-4 shadow-sm">
                <div className="card-header bg-info text-white">
                    <h2 className="mb-0">Borrow Book</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleBorrowBook}>
                        <div className="form-row">
                            <div className="col-md-3">
                                <label htmlFor="user">Select User</label>
                                <select
                                    className="form-control-dd"
                                    id="user"
                                    name="user"
                                    value={selectedUser}
                                    onChange={handleUserChange}
                                    required
                                >
                                    <option value="" disabled>
                                        Select a user
                                    </option>
                                    {users.length > 0 ? (
                                        users.map((user) => (
                                            <option key={user.id} value={user.id}>
                                                {user.name}
                                            </option>
                                        ))
                                    ) : (
                                        <option disabled>Loading users...</option>
                                    )}
                                </select>
                            </div>

                            {/* Dropdown for selecting Book */}
                            <div className="col-md-3">
                                <label htmlFor="book">Select Book</label>
                                <select
                                    id="availableBooks"
                                    className="form-control-dd"
                                    value={selectedBook}
                                    onChange={handleBookChange}
                                    required
                                >
                                    <option value="" disabled>
                                        Select a book
                                    </option>
                                    {availableBooks.length > 0 ? (
                                        availableBooks.map((book) => (
                                            <option key={book.id} value={book.id}>
                                                {book.title}
                                            </option>
                                        ))
                                    ) : (
                                        <option disabled>Loading books...</option>
                                    )}
                                </select>
                            </div>

                            {/* Input for Quantity */}
                            <div className="col-md-3">
                                <label htmlFor="quantity">Quantity</label>
                                <input
                                    type="number"
                                    className="form-control-dd"
                                    id="quantity"
                                    name="quantity"
                                    value={quantity}
                                    onChange={handleQuantityChange}
                                    placeholder="Enter quantity"
                                    required
                                />
                            </div>

                            <div className="col-md-3">
                                <button type="submit" className="btn btn-success mt-4 w-100" style={{ marginTop: '10%', marginLeft: '20%', width: '100%' }}>
                                    Borrow Book
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
                <ToastContainer />
            </div>
        </div>
    );
};

export default BookPage;
