import React, { useState, useEffect } from 'react';
import axios from 'axios';

const User = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ name: '', email: '' });

  // Fetch the list of users from the backend
  useEffect(() => {
    axios
      .get('http://127.0.0.1:8000/api/users') // Adjust URL according to your backend
      .then((response) => setUsers(response.data))
      .catch((error) => console.error('Error fetching users:', error));
  }, []);

  // Handle input changes for the new user form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  // Handle form submission to add a new user
  const handleAddUser = (e) => {
    e.preventDefault();
    if (!newUser.name || !newUser.email) {
      alert('Please enter both name and email');
      return;
    }

    // Send the new user to the backend
    axios
      .post('http://127.0.0.1:8000/api/users', newUser) // Adjust URL according to your backend
      .then((response) => {
        setUsers((prevUsers) => [...prevUsers, response.data]); // Update the user list
        setNewUser({ name: '', email: '' }); // Reset form fields
      })
      .catch((error) => console.error('Error adding user:', error));
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">User Page</h1>

      {/* Form to Add New User */}
      <div className="card mb-4">
        <div className="card-header bg-info text-white">
          <h2 className="mb-0">Add a New User</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleAddUser}>
            <div className="form-row">
              <div className="form-group col-md-6">
                <label htmlFor="name">Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  value={newUser.name}
                  onChange={handleInputChange}
                  placeholder="Enter user name"
                  required
                />
              </div>
              <div className="form-group col-md-6">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  name="email"
                  value={newUser.email}
                  onChange={handleInputChange}
                  placeholder="Enter user email"
                  required
                />
              </div>
            </div>
            <button type="submit" className="btn btn-success mt-3">
              Add User
            </button>
          </form>
        </div>
      </div>

      {/* List of Users */}
      <h2 className="text-center mb-4">Users List</h2>
      <div className="card">
        <div className="card-header bg-primary text-white">
          <h2 className="mb-0">Users</h2>
        </div>
        <div className="card-body">
          <ul className="list-group">
            {users.map((user) => (
              <li key={user.id} className="list-group-item d-flex justify-content-between align-items-center">
                <strong>{user.name}</strong> - {user.email}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default User;
