import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import "react-toastify/dist/ReactToastify.css";

const BookPage = () => {
    const [books, setBooks] = useState([]);
    const [newBook, setNewBook] = useState({ title: '', author: '' });

    const [selectedUser, setSelectedUser] = useState("");
    const [selectedBook, setSelectedBook] = useState("");
    const [quantity, setQuantity] = useState("");

    const [users, setUsers] = useState('');
    const [availableBooks, setAvailableBooks] = useState("");

    const handleBorrowBook = (e) => {
        e.preventDefault();
        const borrowData = {
            user: selectedUser,
            book: selectedBook,
            quantity,
        };

        axios
            .post('http://127.0.0.1:8000/api/borrow/', borrowData)
            .then((response) => {
                toast.success('Book borrowed successfully', {
                    position: toast.POSITION.TOP_RIGHT,  // Make sure POSITION is available
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                });
            })
            .catch((error) => {
                if (error.response) {
                    toast.error(error.response.data.error, {
                        position: toast.POSITION.TOP_RIGHT,
                        autoClose: 5000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                    });
                } else {
                    toast.error("An error occurred. Please try again.", {
                        position: toast.POSITION.TOP_RIGHT,
                        autoClose: 5000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                    });
                }
            });
    };

    useEffect(() => {
        axios
            .get('http://127.0.0.1:8000/api/books/')
            .then((response) => {
                setBooks(response.data);
                setAvailableBooks(response.data);
            })
            .catch((error) => console.error('Error fetching books:', error));
    }, []);

    useEffect(() => {
        axios
            .get('http://127.0.0.1:8000/api/users/')
            .then((response) => setUsers(response.data))
            .catch((error) => console.error('Error fetching users:', error));
    }, []);

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-5 text-primary">Borrow Book</h1>
            <div className="card mb-4 shadow-sm">
                <div className="card-header bg-info text-white">
                    <h2 className="mb-0">Borrow Book</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleBorrowBook}>
                        <div className="form-row">
                            <div className="col-md-3">
                                <label htmlFor="user">Select User</label>
                                <select
                                    className="form-control"
                                    id="user"
                                    value={selectedUser}
                                    onChange={(e) => setSelectedUser(e.target.value)}
                                    required
                                >
                                    <option value="" disabled>Select a user</option>
                                    {users && users.map((user) => (
                                        <option key={user.id} value={user.id}>{user.name}</option>
                                    ))}
                                </select>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="book">Select Book</label>
                                <select
                                    className="form-control"
                                    id="book"
                                    value={selectedBook}
                                    onChange={(e) => setSelectedBook(e.target.value)}
                                    required
                                >
                                    <option value="" disabled>Select a book</option>
                                    {availableBooks && availableBooks.map((book) => (
                                        <option key={book.id} value={book.id}>{book.title}</option>
                                    ))}
                                </select>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="quantity">Quantity</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    id="quantity"
                                    value={quantity}
                                    onChange={(e) => setQuantity(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="col-md-3">
                                <button type="submit" className="btn btn-success mt-4 w-100">
                                    Borrow Book
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
            <ToastContainer />
        </div>
    );
};

export default BookPage;
